(()=>{window.addEventListener("DOMContentLoaded",()=>{let o=new IntersectionObserver(e=>{e.forEach(t=>{let r=t.target.getAttribute("id");t.intersectionRatio>0?document.querySelector(`#TableOfContents ul li a[href="#${r}"]`).classList.add("active"):document.querySelector(`#TableOfContents ul li a[href="#${r}"]`).classList.remove("active")})});document.querySelectorAll("article h3[id], article h2[id]").forEach(e=>{o.observe(e)})});})();
